ta\": {\"embed\": \"https://www.youtube.com/embed/OlQhhNa4D_c\", \"width\": 580, \"height\": 320, \"source\": \"https://youtu.be/OlQhhNa4D_c\", \"caption\": \"\", \"service\": \"youtube\"}, \"type\": \"embed\"}]}'),(9,'Genshin Impact - Game Gacha dành cho Wibu','346971591_640401574609023_7260348714798282656_n.jpg','gacha',15,1,6,'2023-05-30 00:00:00',0,1,'none',1,'{\"blocks\": [{\"id\": \"rB6Q-r3J84\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685409617181.jpg\"}, \"caption\": \"Yoimiya\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}]}'),(10,'Avatar DK - Một thời tuổi thơ','unnamed.png','Quay về tuổi thơ nào anh em',16,1,4,'2023-05-30 00:00:00',1,1,'none',1,'{\"blocks\": [{\"id\": \"NcAsRuK3cu\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685410998951.png\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}, {\"id\": \"gHWU0MIo0_\", \"data\": {\"link\": \"https://www.youtube.com/watch?v=OlQhhNa4D_c\", \"meta\": {}}, \"type\": \"linkTool\"}]}'),(11,'Avatar Musik - Game nhái Audition ?','unnamed (1).png','Quay lại tuổi thơ liệt phím cách với game này',16,1,4,'2023-05-30 00:00:00',0,1,'none',1,'{\"blocks\": [{\"id\": \"d-TAbFqffq\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685411416925.png\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}, {\"id\": \"aWFeD34u5C\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685411425804.jpg\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}]}'),(12,'Hiệp sĩ online - Game Bê Lô Khí Phách Anh Hùng','knightage.jpg','HSO',16,1,4,'2023-05-29 00:00:00',9,1,'none',1,'{\"blocks\": [{\"id\": \"MCkitriTZx\", \"data\": {\"text\": \"Bạn muốn tìm một con game đồ hoạ đẹp ?\"}, \"type\": \"paragraph\"}, {\"id\": \"0FVjAxr8fZ\", \"data\": {\"text\": \"Bạn muốn trải nghiệm một thế giới viễn tưởng đầy màu sắc mang khuynh hướng vùng đất Bắc Âu thời kỳ trung cổ ?\"}, \"type\": \"paragraph\"}, {\"id\": \"X1bMIgVnfb\", \"data\": {\"text\": \"Hãy đến với Hiệp Sĩ online, nơi bạn sẽ trở thành một vị anh hùng đi giải cứu thế giới\"}, \"type\": \"paragraph\"}, {\"id\": \"FJ949Gt0-q\", \"data\": {\"text\": \"Là một game nhập vai trực tuyến – Thời Đại Hiệp Sĩ mang đến cốt truyện hấp dẫn và vui nhộn, thế giới rộng lớn thỏa sức khám phá, hệ thống chiến đấu – vật phẩm đa dạng cùng với 4 lớp nhân vật để bạn lựa chọn.\"}, \"type\": \"paragraph\"}, {\"id\": \"tuqP2xVqdy\", \"data\": {\"text\": \"Thời Đại Hiệp Sĩ là trò chơi trực tuyến đa nền tảng. Bạn có thể chơi được trên máy tính PC Windows, iPhone, Các dòng máy chạy hệ điều hành Android, Windows Phone, và có cả bản Java chạy trên S40, S60 cũ của Nokia. Với chất lượng cao và tốc độ mượt mà trên các loại đường truyền mạng ADSL, 3G, GPRS.\"}, \"type\": \"paragraph\"}, {\"id\": \"jzlJLG3zo_\", \"data\": {\"text\": \"Danh sách các kỹ năng\", \"level\": 3}, \"type\": \"header\"}, {\"id\": \"5FrOllc0q-\", \"data\": {\"text\": \"Chiến binh\"}, \"type\": \"paragraph\"}, {\"id\": \"wQjVwAcYyt\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685411967988.png\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}, {\"id\": \"Pn_y6Hi6zJ\", \"data\": {\"text\": \"Sát thủ\"}, \"type\": \"paragraph\"}, {\"id\": \"47d-v8T4j-\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685411974102.png\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}, {\"id\": \"eJy_1cs1zh\", \"data\": {\"text\": \"Thế giới rộng lớn\", \"level\": 3}, \"type\": \"header\"}, {\"id\": \"IuXwg1mbQO\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685412020688.png\"}, \"caption\": \"map tổng\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}, {\"id\": \"IwQiQnSyl8\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685412024911.png\"}, \"caption\": \"ven làng\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}, {\"id\": \"k3z-XfZiuU\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685412029252.png\"}, \"caption\": \"hang động\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}, {\"id\": \"yoEe6_manQ\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685412034410.png\"}, \"caption\": \"bờ biển\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}, {\"id\": \"G8Ixt5jEUM\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685412039201.png\"}, \"caption\": \"đầm lầy\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}, {\"id\": \"zKD7yE3mRo\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685412047920.png\"}, \"caption\": \"chiến trường giun\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}]}'),(14,'Kiếm thế Online - Game tru tiên mới','trutien.jpg','Chơi đi anh em, cuốn lắm',16,2,4,'2023-05-26 00:00:00',2,1,'none',1,'{\"blocks\": [{\"id\": \"4oVlGXDjdP\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685412446248.jpg\"}, \"caption\": \"caption\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}]}'),(17,'Genshin Impact: Hướng dẫn build Nilou, \"siêu sao\" mới của Teyvat khiến Zhongli phải chào thua','nilou-1.jpg','Genshin Impact: Hướng dẫn build Nilou, \"siêu sao\" mới của Teyvat khiến Zhongli phải chào thua',4,2,4,'2023-05-27 00:00:00',6,1,'none',1,'{\"blocks\": [{\"id\": \"6z78Gy8G4t\", \"data\": {\"text\": \"Trong bài viết này, Sforum xin được chia sẻ cùng bạn cách build Nilou để tiêu diệt mọi thể loại quái lẫn boss và La Hoàn trong game. Cách build và sử dụng Nilou sẽ khá phức tạp do bộ kỹ năng độc đáo của nhân vật này, hãy theo dõi kỹ bài viết nhé!\"}, \"type\": \"paragraph\"}, {\"id\": \"4uAsy-5djT\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685416623120.jpg\"}, \"caption\": \"Nếu bạn là một wibu “thứ thiệt” và có niềm đam mê mãnh liệt với bộ môn gacha thần thánh trong Genshin Impact, thì hãy tham gia ngay cộng đồng Discord của S-Games. Tại đây bạn có thể tán dóc, trao đổi, chia sẻ kinh nghiệm với những người chơi khác để build được nhân vật hay đội hình thích hợp, sẵn sàng khám phá thế giới Teyvat tuyệt đẹp, Ngoài ra, những minigame cùng phần quà hấp dẫn đang chờ đợi bạn tại Discord của S-Games\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}, {\"id\": \"Gb09TqWaqQ\", \"data\": {\"text\": \"Nilou làm được gì?\", \"level\": 3}, \"type\": \"header\"}, {\"id\": \"gEmeiggXHb\", \"data\": {\"text\": \"Trong thời điểm hiện tại, Nilou cho thấy mình được thiết kế để chơi trong đội hình thuần Thảo + Thủy, và gây sát thương bằng các Hạt Nhân Căng Tròn được tạo ra bởi phản ứng Sum Xuê đặc biệt của mình. Dù không thể được dùng cho Nở Rộ và Bung Tỏa, Hạt Nhân Căng Tròn của Nilou lại có tầm AOE lớn hơn và sát thương cao hơn.\"}, \"type\": \"paragraph\"}, {\"id\": \"qo5XSNBOVJ\", \"data\": {\"embed\": \"https://www.youtube.com/embed/P5DeAD_uXE0\", \"width\": 580, \"height\": 320, \"source\": \"https://youtu.be/P5DeAD_uXE0\", \"caption\": \"\", \"service\": \"youtube\"}, \"type\": \"embed\"}, {\"id\": \"Oz4hEBEwxb\", \"data\": {\"text\": \"Đặc biệt, nhờ vào thiên phú Sảnh Đường Ngân Nga mà tất cả nhân vật trong đội đều có khả năng tạo ra Hạt Nhân Căng Tròn chứ không phải chỉ mình Nilou. Vì vậy, để có thể tối ưu sát thương của Nilou, game thủ nên để cho nhân vật có chỉ số tinh thông nguyên tố cao nhất trong đội hình kích hoạt phản ứng Sum Xuê. Khi chơi theo hướng này, Nilou sẽ là một nhân vật cấp Thủy từ ngoài sân, nhường chỗ cho nhân vật khác đứng sân kích phản ứng.\"}, \"type\": \"paragraph\"}, {\"id\": \"hBNAm0XSrx\", \"data\": {\"text\": \"Vào thời điểm hiện tại, chúng ta chỉ có 3 nhân vật hệ Thảo là Tighnari, Collei và Nhà Lữ Hành, nhưng cả 3 đều không thực sự lý tưởng cho Nilou. Đội hình của cô nàng có lẽ sẽ chỉ hoàn thiện khi Thảo Thần Nahida ra mắt vào ngày đầu tiên của patch 3.2 (dự kiến là 3/11/2022).\"}, \"type\": \"paragraph\"}]}'),(18,'Genshin Impact: Top đội hình mạnh để build cùng Raiden Shogun','cach-choi-raiden-genshin-impact-thong-tin-guide-skill-moi-thumb-640x360.jpg','Cách build đội hình Raiden Shogun trong Genshin Impact như thế nào để phát huy tối đa sức mạnh của các nhân vật trong toàn đội hình. Xây dựng team Raiden Shogun sẽ ảnh hưởng đến chiến thuật của toàn trận đấu khi chơi Genshin Impact.',4,3,4,'2023-05-29 00:00:00',6,1,'active',1,'{\"blocks\": [{\"id\": \"vDihKMV7Xv\", \"data\": {\"text\": \"Cách buil team của mỗi người chơi là hoàn toàn khác nhau, tuy nhiên đội hình Raiden Shogun nào mới mang đến sức mạnh hủy diệt đối thủ lại là vấn đề vô cùng đau đầu đối với các game thủ. Tuy nhiên Download.vn sẽ gửi đến các bạn một số gợi ý cho việc buil team Raiden Shogun để phát huy được sức mạnh tốt nhất cho tất cả thành viên.\"}, \"type\": \"paragraph\"}, {\"id\": \"gsFliHF8fH\", \"data\": {\"text\": \"Genshin Impact là một trong số game gacha được yêu thích nhất hiện nay. Với thiết kế đồ họa tuyệt đẹp cùng lối chơi khám phá đấu boss hấp dẫn, game luôn mang tới cho người chơi nhiều bất ngờ thú vị. Một trong số nhân tố quan trọng khi chơi game là xây dựng đội hình nhân vật. Bài viết này sẽ tập trung vào Raiden Shogun.\"}, \"type\": \"paragraph\"}, {\"id\": \"jU0DyNLnIw\", \"data\": {\"text\": \"Genshin Impact là một trong số game gacha được yêu thích nhất hiện nay. Với thiết kế đồ họa tuyệt đẹp cùng lối chơi khám phá đấu boss hấp dẫn, game luôn mang tới cho người chơi nhiều bất ngờ thú vị. Một trong số nhân tố quan trọng khi chơi game là xây dựng đội hình nhân vật. Bài viết này sẽ tập trung vào Raiden Shogun.\"}, \"type\": \"paragraph\"}, {\"id\": \"AyFh1rbXyV\", \"data\": {\"text\": \"Mặc dù Raiden Shogun đã có mặt trong game từ trước, tuy nhiên, một số sự kiện trong Genshin Impact đã đưa nhân vật này trở thành tâm điểm một lần nữa. Một Archon Quest mới đã đưa câu chuyện về Raiden Shogun đi xa hơn. Một boss mới sẽ chiến đấu với một phiên bản Raiden Shogun kỳ lạ. Nếu sở hữu nhân vật này, nên ghép đội hình Raiden Shogun như thế nào để giành chiến thắng trong mọi trận đấu?\"}, \"type\": \"paragraph\"}, {\"id\": \"zX948xr_Cb\", \"data\": {\"text\": \"Bên cạnh đó cũng còn rất nhiều đội hình kết hợp với Raiden Shogun tạo nên sự hoàn hảo mà chúng tôi chưa thể liệt kê hết, cũng như chưa có cơ hội trải nghiệm. Thông qua các kỹ năng, đặc điểm nhân vật chắc chắn mỗi người sẽ có cho mình 1 đội Genshin Impact Raiden Shogun tốt nhất.\"}, \"type\": \"paragraph\"}, {\"id\": \"9XjWN-oEvy\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685417307472.jpg\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}, {\"id\": \"toQL8tcDnk\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685417311575.jpg\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}, {\"id\": \"3lInotJcb9\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685417317854.jpg\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}]}'),(21,'Hướng dẫn chơi Kiếm trong Ninja','tai-ninja-school-thieu-nien-anh-hung-242482-logo-15-06-2021.png','kiếm',5,2,4,'2023-01-01 00:00:00',1,0,'none',1,'{\"blocks\": [{\"id\": \"-DLhXlvXpe\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685418780678.jpg\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}]}'),(22,'Vũ Minh Hiếu - Game thủ xuất thân từ ĐH Giao Thông Vận Tải','avatar.jpg','Anh ấy đã đạt mức rank Cao thủ trong Liên quân Mobile',18,3,4,'2000-12-26 00:00:00',3,1,'none',1,'{\"blocks\": [{\"id\": \"0FQfxx_w21\", \"data\": {\"text\": \"raiden\"}, \"type\": \"paragraph\"}, {\"id\": \"iu6Ce69HMU\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685457194308.jpg\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}]}'),(23,'daokiem','ban-tin-teyvat-ky-01.jpg','aaa',3,2,4,'2012-12-12 00:00:00',0,0,'none',1,'{\"blocks\": []}'),(25,'Hướng dẫn chơi Phô Lô Ti Nô','pholo.jpg','Phô lô ti nô',4,2,4,'2023-05-29 00:00:00',3,1,'none',1,'{\"blocks\": [{\"id\": \"Gof6-ZsqRp\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685432318257.jpg\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}]}'),(26,'Hướng dẫn chơi veres trong Liên quân mobile','huong-dan-choi-veres-mua-14-bang-ngoc-phu-hieu-va-cach-choi-thumb2-800x450.jpg','Đây là vị tướng rất khó chơi đòi hỏi kĩ năng cao',4,2,4,'2023-05-27 00:00:00',4,1,'none',1,'{\"blocks\": [{\"id\": \"DKQ3UHHOZW\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685427091609.jpg\"}, \"caption\": \"Đấu sĩ vẻ\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}, {\"id\": \"h2bn82ko4H\", \"data\": {\"text\": \"I. Điểm mạnh và điểm yếu của Veres&nbsp;\", \"level\": 3}, \"type\": \"header\"}, {\"id\": \"0TgsO1hqxT\", \"data\": {\"text\": \"1. Điểm mạnh&nbsp;\", \"level\": 4}, \"type\": \"header\"}, {\"id\": \"WrzABr8Y8O\", \"data\": {\"text\": \"Khi tích đủ 5 điểm, Veres sẽ kích hoạt nội tại huyết xích giúp hồi máu và miễn khống chế\"}, \"type\": \"paragraph\"}, {\"id\": \"6DmbSot9CH\", \"data\": {\"text\": \"Bộ chiêu thức 1 + 2 đều hỗ trợ tích thêm điểm cho nội tại, đồng thời nội tại trúng tướng cũng giúp hồi chiêu\"}, \"type\": \"paragraph\"}, {\"id\": \"gWgJIJ1S0d\", \"data\": {\"text\": \"2. Điểm yếu&nbsp;\", \"level\": 4}, \"type\": \"header\"}, {\"id\": \"hbkGnRzVET\", \"data\": {\"text\": \"Bộ chiêu thức của Veres khá khó trúng mục tiêu, nếu sử dụng không chính xác thì Veres sẽ dễ dàng “bốc hơi” trong giao tranh.\"}, \"type\": \"paragraph\"}, {\"id\": \"_Q-NbEzUyh\", \"data\": {\"text\": \"Chiêu thức của Veres&nbsp;chỉ mạnh khi áp sát, vì vậy Veres khá sợ những vị tướng khống chế tầm xa\"}, \"type\": \"paragraph\"}, {\"id\": \"-gdfistWvt\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685427710483.jpg\"}, \"caption\": \"Bảng ngọc\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}, {\"id\": \"xQBZbnCD5E\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685427718782.jpg\"}, \"caption\": \"Bảng nâng chiêu\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}]}'),(27,'Giftcode Genshin 3.0','Splashscreen_The_Morn_a_Thousand_Roses_Brings.webp','Dưới đây là mã quà tặng phiên bản 3.0',9,1,4,'2023-05-30 00:00:00',0,1,'none',1,'{\"blocks\": [{\"id\": \"Y2BfJWoDls\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685427971078.webp\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}, {\"id\": \"gQuL_MReC9\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685427979105.webp\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}]}'),(28,'Giftcode Genshin 3.1','3.1.png','Dưới đây là Giftcode Genshin phiên bản 3.1',9,2,6,'2023-05-29 00:00:00',2,1,'none',1,'{\"blocks\": [{\"id\": \"EqP6nq7j-e\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685428707033.webp\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}]}'),(29,'Giftcode Genshin 3.2','3.2.png','Dưới đây là Giftcode Genshin phiên bản 3.2',9,2,6,'2023-05-27 00:00:00',2,1,'none',1,'{\"blocks\": [{\"id\": \"MQO4gq33Hb\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685428719879.webp\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}]}'),(30,'Giftcode Genshin 3.3','3.3.png','Dưới đây là Giftcode Genshin phiên bản 3.3',9,2,6,'2023-05-29 00:00:00',0,1,'none',1,'{\"blocks\": [{\"id\": \"JTNLGLWSj_\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685428758349.webp\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}]}'),(31,'Giftcode Genshin 3.4','3.4.png','Dưới đây là Giftcode Genshin phiên bản 3.4',9,2,6,'2023-05-29 00:00:00',0,1,'none',1,'{\"blocks\": [{\"id\": \"F0UgQTr0JE\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685428780716.webp\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}]}'),(32,'Giftcode Genshin 3.5','3.5.png','Dưới đây là Giftcode Genshin phiên bản 3.5',9,2,6,'2023-05-29 00:00:00',0,1,'none',1,'{\"blocks\": [{\"id\": \"Y9AItda1iA\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685428791547.webp\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}]}'),(33,'Giftcode Genshin 3.6','3.6.png','Dưới đây là Giftcode Genshin phiên bản 3.6',9,2,6,'2023-05-29 00:00:00',0,1,'none',1,'{\"blocks\": [{\"id\": \"bCDNNvhCO9\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685428809836.webp\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}]}'),(34,'Hướng dẫn chơi Lindis LMHT','lindis.jpg','Lindis',5,3,4,'2021-02-01 00:00:00',0,1,'none',1,'{\"blocks\": [{\"id\": \"xPXa3g0ymL\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685432513878.jpg\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}, {\"id\": \"CdkflxJMoU\", \"data\": {\"embed\": \"https://www.youtube.com/embed/sED8iRqGmJo\", \"width\": 580, \"height\": 320, \"source\": \"https://youtu.be/sED8iRqGmJo\", \"caption\": \"\", \"service\": \"youtube\"}, \"type\": \"embed\"}]}'),(35,'Hướng dẫn chơi Vanhein Liên Quân Mobile','vanhein.jpg','Vanhein',6,3,4,'2023-05-31 00:00:00',0,1,'none',1,'{\"blocks\": [{\"id\": \"-iQqzt-E5A\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685433061611.jpg\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}]}'),(36,'Hướng dẫn chơi Yoto Hime Naraka:Blade Point','1920x1080 (1).jpg','Yotochi',6,3,4,'2023-05-31 00:00:00',0,1,'none',1,'{\"blocks\": [{\"id\": \"IDjB3Qgkde\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685433137697.jpg\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}]}'),(37,'Gửi anh em Bộ hình nền cho máy tính cực đẹp','shiranui.png','Hình nền lấy từ game Âm dương sư nhé anh em',18,3,4,'2023-05-31 00:00:00',2,1,'none',1,'{\"blocks\": [{\"id\": \"cYnb8I828h\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685433687974.jpg\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}, {\"id\": \"9zkGBsWu-F\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685433692546.jpg\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}, {\"id\": \"Nmxf8Mq8XO\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685433696760.jpg\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}, {\"id\": \"8Oz18BOzJP\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685433700494.jpg\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}, {\"id\": \"83cx3Nl6VL\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685433704221.jpg\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}, {\"id\": \"2g5CGyDke1\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685433707764.jpg\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}, {\"id\": \"RW09DwAHxX\", \"data\": {\"link\": \"https://play.google.com/store/apps/details?id=com.netease.onmyoji.gb&hl=vi&gl=US&pli=1\", \"meta\": {}}, \"type\": \"linkTool\"}]}'),(38,'Mortal Kombat 11 – Đánh Giá Game','mortal-kombat-11-danh-gia-game-11.jpg','Mortal Kombat 11 – Khi ra mắt vào năm 1995, phiên bản Mortal Kombat đầu tiên đã gây nên một “cơn sốt” vào thời điểm bấy giờ khi gián tiếp… tạo nên cơ quan thẩm định video game ESRB của Mỹ. Đến nay, dòng game này vẫn đang được xem là một trong những thương hiệu video game tai tiếng nhất của nền công nghiệp game nhờ vào mức độ bạo lực gây sốc của nó.  Năm 2019, Mortal Kombat 11 lại ra mắt và cũng lại mắc thêm “scandal” mới. Thế nhưng lần này, nó lại liên đới đến môi trường làm việc thiếu lành mạnh dành cho nhân viên tại NetherRealm Studios, cũng như những lùm xùm xoay quanh yếu tố cày cuốc quá độ vốn không có chỗ đứng trong một tựa game AAA. Và đó quả là một vết nhơ đáng tiếc, bởi đây có thể được xem là phiên bản Mortal Kombat hiện đại tròn vai nhất kể từ khi dòng game được tái khởi động từ năm 2011 cho tới nay.',7,3,6,'2023-06-01 00:00:00',1,1,'none',1,'{\"blocks\": [{\"id\": \"_tAtOMTC-k\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685442672338.jpg\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}, {\"id\": \"jwTahZXTpe\", \"data\": {\"text\": \"Mortal Kombat 11 hoạt động theo cách mà bạn mong đợi một tựa game đối kháng 2D hoạt động, trong đó đánh trúng kẻ thù chỉ chiếm 1/5 công việc mà bạn phải thực hiện để thắng một trận đấu.\"}, \"type\": \"paragraph\"}, {\"id\": \"uRLzndNGRn\", \"data\": {\"text\": \"Ra đòn thấp để nhanh chóng “poke” địch, ra đòn cao có tầm xa tốt hơn nhưng diễn ra chậm hơn, đòn phủ đầu (Overhead) dùng để phá thế đỡ ở tư thế ngồi. Bài toán phản xạ bắt đầu mở rộng với các đòn đánh tầm xa, đòn đặc biệt (Special Move) và các combo có thể được thi triển tùy vào khả năng tâng người (juggle) của nhân vật, cũng như kỹ năng hủy combo thường bằng đòn đặc biệt của người chơi. Vượt qua giai đoạn này và bạn sẽ phải “đối phó” với những khái niệm mới: Breakaway dùng để thoát trạng thái bị tâng người, Amplify dùng để kéo dài hoặc gia tăng công dụng của một số đòn đặc biệt, lăn người trong lúc “đo đất” để thoát khỏi tình trạng bị dồn vào góc hẹp, đâu là những đòn đánh được cho là an toàn hay rủi ro và đâu là những đòn đánh dễ bị trừng phạt…\"}, \"type\": \"paragraph\"}, {\"id\": \"5ISk_uclT8\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685442689848.webp\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}, {\"id\": \"YqoG5nWsea\", \"data\": {\"text\": \"Lượng thông tin mà Mortal Kombat 11 truyền tải về cơ chế của nó có thể hơi quá mức dành cho những người mới chơi. Thế nên trò chơi cũng tinh ý hỗ trợ một phần hướng dẫn “tận răng” đầy chi tiết từ cơ bản đến nâng cao, thậm chí còn mang trở lại các chỉ dẫn nhanh cho từng nhân vật từ Injustice 2. Đến cả một số cơ chế ẩn hay khía cạnh mà thông thường bạn sẽ phải tự mò lấy trên diễn đàn game hay Youtube (như Frame Data chẳng hạn) cũng được đưa vào game. Bấy nhiêu đó là quá đủ để bảo toàn những đấu sỹ “chân ướt chân ráo” vào Mortal Kombat 11.\"}, \"type\": \"paragraph\"}, {\"id\": \"7acUNVxcc4\", \"data\": {\"text\": \"Nó có thể không phải là tựa game đối kháng “hardcore” nhất ở thị trường hiện nay, thế nhưng cảm giác thỏa mãn qua mỗi chiến thắng của một trận song đấu vẫn còn đó. Nhịp độ của Mortal Kombat 11 chậm, chậm hơn nhiều so với hai phiên bản trước. Game mang đến nhiều cơ hội phản công dành cho bên chịu đòn hơn, không chỉ qua một cơ chế Fatal Blow mang tính “clutch” cực kỳ cao, mà còn với Flawless Block tưởng thưởng cho phản xạ tốt. Các đòn combo dài hơi vẫn yêu cầu người chơi tự mình khám phá, và bất kỳ ai cũng có quyền ngạo nghễ khi nhuần nhuyễn trổ combo 10 đòn đánh của mình.\"}, \"type\": \"paragraph\"}, {\"id\": \"5iF_UOHbck\", \"data\": {\"text\": \"Và… dĩ nhiên hãy nhớ là, không ai cấm cản bạn “spam” combo dịch chuyển với variation Searing Rage hay bất kỳ thứ gì mà bạn sáng tạo được. Chỉ có kẻ địch mới phải trách chính họ khi không tìm được cách trừng phạt bạn mà thôi.\"}, \"type\": \"paragraph\"}]}'),(39,'Sekiro: Shadows Die Twice – Đánh Giá Game','sekiro-shadow-die-twice-danh-gia-game-161.jpg','Sekiro: Shadows Die Twice – Đánh Giá Game',19,2,4,'2023-05-27 00:00:00',212,1,'none',1,'{\"blocks\": [{\"id\": \"a68cNZ-08w\", \"data\": {\"text\": \"Sekiro: Shadows Die Twice – “Tựa game hành động nhập vai khó nhất hành tinh!” – Có lẽ đây từng là một mô tả phổ biến khi nghe ai đó nói về “huyền thoại” mang tên Dark Souls.\"}, \"type\": \"paragraph\"}, {\"id\": \"vOz5ctz2tM\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685453399810.jpg\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}, {\"id\": \"x7YKCHsqvv\", \"data\": {\"text\": \"Cho đến bây giờ, Dark Souls đã trải qua 3 phiên bản, khép lại một biên niên sử về ngọn lửa sáng thế thì cũng vẫn là một dòng game đầy thử thách về độ khó.\"}, \"type\": \"paragraph\"}, {\"id\": \"g3UdI0IbuP\", \"data\": {\"text\": \"Nhưng cũng đã 2 năm rồi kể từ ngày DLC The Ringed City của Dark Souls III ra mắt, có lẽ Dark Souls nên lui về quá khứ và nhường sân khấu lại cho một siêu phẩm mới mang tên Sekiro: Shadows Die Twice.\"}, \"type\": \"paragraph\"}, {\"id\": \"Bbj0upV4dt\", \"data\": {\"text\": \"Dark Souls từng được xem là tựa game hành động nhập vai khó nhất hành tinh do nhiều lý do, nhưng hiện tại, có lẽ cái danh hiệu không mấy thân thiện với game thủ đó nên được Sekiro: Shadows Die Twice kế thừa thì hơn.\"}, \"type\": \"paragraph\"}, {\"id\": \"I1dWrGA41s\", \"data\": {\"text\": \"Đó có lẽ cũng là đặc điểm dễ nhận biết mà bất kỳ một tựa game đến từ FromSoftware nào cũng có, từ Bloodborne, Dark Souls, Demon’s Souls… cho nên Sekiro: Shadows Die Twice chắc chắn không phải là một ngoại lệ.\"}, \"type\": \"paragraph\"}, {\"id\": \"dYo-t2-Oxe\", \"data\": {\"text\": \"Nhưng khó đến độ hàng nghìn game thủ phải la ó, kiến nghị FromSoftware nên thêm Easy Mode để game để nhiều người có thể trải nghiệm game hơn thì có thể thấy Sekiro: Shadows Die Twice có vẻ như đã nâng độ khó của mình lên một đẳng cấp khác so với các đàn anh cùng nhà rồi.\"}, \"type\": \"paragraph\"}, {\"id\": \"kW0vqdYcQe\", \"data\": {\"embed\": \"https://www.youtube.com/embed/gaD7Fc99aOA\", \"width\": 580, \"height\": 320, \"source\": \"https://youtu.be/gaD7Fc99aOA\", \"caption\": \"Anh em cùng xem thử nhé\", \"service\": \"youtube\"}, \"type\": \"embed\"}, {\"id\": \"abckcg4y7h\", \"data\": {\"text\": \"Dù sao thì cũng chính sự khó nhằn trong các thử thách mà những tựa game đến từ FromSoftware mà những cộng đồng người hâm mộ luôn luôn ngóng chờ các sản phẩm mới đến từ hãng, để không chỉ chinh phục mà còn thách-thức-ngược-lại-chính-những-thử-thách mà các tựa game này đưa ra.\"}, \"type\": \"paragraph\"}, {\"id\": \"UIekw60CBp\", \"data\": {\"text\": \"Và để biết Sekiro: Shadows Die Twice khó đến đâu, hấp dẫn đến đâu và có xứng đáng trở thành người kế thừa tinh thần của thể loại Souls-Borne lừng danh hay không, Vietgame.asia sẽ cùng bạn “vượt cạn” những chông gai cùng chàng nhẫn giả Độc Thủ Lang – Sekiro.\"}, \"type\": \"paragraph\"}, {\"id\": \"0xSYANcawP\", \"data\": {\"text\": \"BẠN SẼ THÍCH\", \"level\": 3}, \"type\": \"header\"}, {\"id\": \"tUEYDMPi8K\", \"data\": {\"text\": \"Nếu là một người hâm mộ trung thành của Dark Souls, vốn có nhịp độ chiến đấu chậm và đòi hỏi người chơi cẩn trọng trong từng hành động, hoặc kể cả Bloodborne với tiết tấu nhanh hơn, thì những ấn tượng đầu tiên với Sekiro: Shadows Die Twice sẽ là cực kỳ phấn khích!\"}, \"type\": \"paragraph\"}, {\"id\": \"9nqnpSEZyx\", \"data\": {\"text\": \"Cơ chế lối chơi chiến đấu đặc trưng của các đàn anh đã gần như được nhà sản xuất thay đổi hoàn toàn và thay thế vào đó là một hệ thống chiến đấu mới đầy năng lượng, giàu cảm xúc trong mỗi pha tung chiêu, gây phấn khích tột độ trong quá trình chiến đấu.\"}, \"type\": \"paragraph\"}, {\"id\": \"oMS_Eh00NX\", \"data\": {\"text\": \"Kể cả khi đã là một tay chơi lão luyện Souls-Borne, thì lối chơi của Sekiro: Shadows Die Twice vẫn sẽ buộc game thủ phải “tẩy não” trước khi bước vào những pha đối đấu đầy nghẹt thở của mình.\"}, \"type\": \"paragraph\"}, {\"id\": \"WSgsWBx83k\", \"data\": {\"text\": \"Đầu tiên, trên vai trò là một nhẫn giả (shinobi) có thân pháp nhanh nhẹn và kỹ năng chiến đấu hiểm hóc, nhân vật chính có một khả năng di chuyển hết sức linh hoạt cả về tốc độ lẫn phạm vi hoạt động.\"}, \"type\": \"paragraph\"}, {\"id\": \"p8UAyLyKsg\", \"data\": {\"text\": \"Giờ đây nhân vật chính không chỉ có tốc độ di chuyển trên mặt đất rất nhanh mà các pha hành động cũng trở nên cực kỳ dồn dập và tạo được nhịp độ cao bởi thanh sức bền đã được loại bỏ hoàn toàn.\"}, \"type\": \"paragraph\"}, {\"id\": \"dVNFKSMeBC\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685452913370.webp\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}, {\"id\": \"co9vaQzhlp\", \"data\": {\"text\": \"Game thủ không còn bị kìm hãm sự “hăng máu” trong chiến đấu như trước mà ngược lại&nbsp;Sekiro: Shadows Die Twice&nbsp;còn khuyến khích bạn phải liên tục, liên tục chiến đấu không ngừng nghỉ chỉ để chớp lấy một khắc thời cơ mang tính quyết định.\"}, \"type\": \"paragraph\"}, {\"id\": \"4nleXyffGm\", \"data\": {\"text\": \"Ngược lại, mặc dù thoải mái hơn khi không có sự góp mặt của thanh sức bền nhưng cơ chế chiến đấu của&nbsp;Sekiro: Shadows Die Twice&nbsp;lại khó theo một cách khác: thanh Posture – Tấn pháp.\"}, \"type\": \"paragraph\"}, {\"id\": \"SZCwrZiv6_\", \"data\": {\"text\": \"Thanh Posture này tuy là một khái niệm mới nhưng cách hoạt động không khác thanh stamina cũ là mấy, ở chỗ nếu để thanh Posture này đầy đồng nghĩa với bạn mất khả năng đỡ đòn lẫn di chuyển và CHẾT CHẮC nếu trúng đòn kết liễu (Death Blow).\"}, \"type\": \"paragraph\"}, {\"id\": \"GjZ3bKuNSa\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685452936464.webp\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}, {\"id\": \"TmPTe76tuH\", \"data\": {\"text\": \"Cuộc đấu giữa nhân vật chính và đối thủ không còn đặt trọng tâm ở thanh máu nữa mà được nhường chỗ hoàn toàn cho không chỉ một mà tới hai thanh Posture, một của bạn và một của đối thủ.\"}, \"type\": \"paragraph\"}, {\"id\": \"THuzV-ggGg\", \"data\": {\"text\": \"“Một khắc thời cơ” mà người viết nói đến phía trên chính là thời khắc mà bạn sẽ phải tung đòn hoặc phản đòn chính xác để làm đầy thanh Posture của đối thủ.\"}, \"type\": \"paragraph\"}, {\"id\": \"BP0lP91VqZ\", \"data\": {\"text\": \"Mà khi đã nói đến “thời khắc” tức chúng chỉ xuất hiện trong một cái chớp mắt vài phần trăm giây… lỡ một nhịp thôi, tình thế có thể bị lật ngược hoàn toàn.\"}, \"type\": \"paragraph\"}, {\"id\": \"8OqD8IMaVL\", \"data\": {\"text\": \"Ừ, có thể bạn là một tay kiếm lão luyện, tay không giết sạch Lothric Castle, nhắm mắt đùa giỡn với Nameless King, hay đối mặt với Midir khi chỉ mặc một cái quần chip… Nhưng chuyện đó xưa quá rồi!\"}, \"type\": \"paragraph\"}, {\"id\": \"etROrt1CmR\", \"data\": {\"text\": \"Lối chơi cải tiến này buộc Sekiro: Shadows Die Twice mang tính đột phá cực cao khi không chỉ bơm vào người chơi một lượng “andrenaline” dồi dào khiến game thủ không chỉ dính chặt vào các trận chiến nghẹt thở đến tột độ mà còn phải bình tĩnh duy trì sự tập trung để né, đỡ đòn, tung đòn đáp trả làm đầy thanh Posture của đối thủ, cũng như cẩn thận với chính thanh Posture của bản thân.\"}, \"type\": \"paragraph\"}, {\"id\": \"OhR4up4idJ\", \"data\": {\"text\": \"Ngoài ra, khi cuộc đấu xoay quanh hai thanh Posture thì cũng có nhiều cách đến làm đầy thanh Posture của đối thủ như gây sát thương, đỡ đòn đúng thời điểm, hoặc phản đòn những chiêu “chữ đỏ” tất sát… Một khi đã làm chủ được tất cả những kỹ năng về đỡ đòn hay phản đòn chính xác thì có khi đối thủ sẽ gục sớm trước khi người chơi kịp đánh trả!\"}, \"type\": \"paragraph\"}, {\"id\": \"qrmuPfaZLT\", \"data\": {\"text\": \"Dĩ nhiên, trường hợp chưa đánh đã thắng nói trên chỉ đúng khi bạn nắm rõ đấu pháp của đối thủ, cũng như có thể đỡ được một chiêu tất sát trong một cái chớp mắt, thêm gần chục cú ra đòn liên tiếp cách nhau khoảng 0.2s và một loạt ám khí, độc dược… ném thẳng vào mặt ngay sau đó.\"}, \"type\": \"paragraph\"}, {\"id\": \"mg9ADZOhJ4\", \"data\": {\"text\": \"Nếu làm được thì cũng xin chúc mừng bạn vừa vượt qua được một con… trùm phụ của Sekiro: Shadows Die Twice!\"}, \"type\": \"paragraph\"}, {\"id\": \"WUXcsTAusD\", \"data\": {\"text\": \"Còn nữa, đừng nghĩ việc trốn chạy, nhào lộn và lẩm bẩm những câu “thần chú” khi đối diện một con trùm trong Sekiro: Shadows Die Twice sẽ giúp bạn sống lâu hơn một chút.\"}, \"type\": \"paragraph\"}, {\"id\": \"LD7fgtfM8N\", \"data\": {\"text\": \"Bởi tạo khoảng cách với trùm cũng đồng nghĩa với cuộc đấu sẽ trở thành cuộc chơi mèo-vờn-chuột đúng nghĩa, và chắc chắn bạn chính là con chuột xấu số đó!\"}, \"type\": \"paragraph\"}, {\"id\": \"DuBXT-6yAK\", \"data\": {\"text\": \"Đến đây, người viết cũng có một “fun-fact” nhỏ cho bạn đọc rằng: Chỉ nội trong đoạn dạo đầu, Sekiro: Shadows Die Twice đã bố trí cho bạn tận… 3 con trùm phụ (tutorial-boss) để tập làm quen với cơ chế chiến đấu của game đấy! – Một tập cho bạn kỹ năng né tránh (Ogre), một tập cho bạn kỹ năng đỡ đòn và phản đòn (General Naomori Kawarada), và đặc biệt là một tập cho bạn kỹ năng tư duy làm chủ đấu trường (General Tenzen Yamauchi) trước khi lâm trận.\"}, \"type\": \"paragraph\"}, {\"id\": \"2hE_9sqGQG\", \"data\": {\"text\": \"Đánh đấm bằng cơ chế mới tuy cực kỳ đã tay và hưng phấn, nhưng chắc chắn độ khó của những đối thủ mang lại cho người chơi cũng không khác những màn tra tấn là mấy.\"}, \"type\": \"paragraph\"}, {\"id\": \"Nmkk_2Ai00\", \"data\": {\"text\": \"Nên nếu chọn đối đầu một cách “sòng phẳng” thì có lẽ đó là một sự lựa chọn không được “sáng dạ” cho lắm trong Sekiro: Shadows Die Twice.\"}, \"type\": \"paragraph\"}, {\"id\": \"qJkeXDg2TM\", \"data\": {\"text\": \"Sekiro: Shadows Die Twice thêm vào một cơ chế đu dây bắn ra từ cánh tay máy của Độc Thủ Lang và cho phép anh có những bước di chuyển lớn để “tháo chốt” những điểm nóng trong khu vực, vừa cho phép anh có một góc nhìn bao quát hơn để đưa ra chiến thuật tiếp cận trong chiến đấu.\"}, \"type\": \"paragraph\"}, {\"id\": \"1jVYTEudCO\", \"data\": {\"text\": \"Chẳng hạn trong một khu vực có sự xuất hiện của một con trùm phụ và hàng chục tên “lính lác tò te”, người chơi sẽ có hai cách: một là bỏ qua nó, hai là tiêu diệt để thu thập nguyên liệu nâng cấp chỉ số cho bản thân.\"}, \"type\": \"paragraph\"}, {\"id\": \"klDdnetjfe\", \"data\": {\"text\": \"Nếu may mắn thì bạn sẽ chạy thoát khỏi con trùm phụ và tiến vào khu vực mới, không thì sẽ bị “tấp” hội đồng.\"}, \"type\": \"paragraph\"}, {\"id\": \"TDN2Wy_TSF\", \"data\": {\"text\": \"Nếu chọn cách “clear map” (quét sạch) thì đó là lúc hệ thống hành động bí mật của Sekiro: Shadows Die Twice lên ngôi.\"}, \"type\": \"paragraph\"}, {\"id\": \"M9trbn9__L\", \"data\": {\"text\": \"Chiến thuật hành động lén lút là một chiếc chìa khóa giúp các màn chơi của Sekiro: Shadows Die Twice trở nên dễ thở hơn, cũng như hấp dẫn hơn theo một cách riêng tách biệt khỏi các đàn anh Souls-Borne.\"}, \"type\": \"paragraph\"}, {\"id\": \"vivHf0UDGg\", \"data\": {\"text\": \"Ở đây đòi hỏi người chơi nên biết tư duy, thử đi thử lại nhiều kế hoạch khác nhau để đạt hiệu quả tốt nhất mà không bỏ mạng.\"}, \"type\": \"paragraph\"}, {\"id\": \"6A8vNbbKaI\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685452984210.webp\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}, {\"id\": \"HquHb20ydY\", \"data\": {\"text\": \"Để hiểu rõ hơn, lấy vị dụ một con trùm phụ Juzou The Drunkard trong màn Hirata Estate.\"}, \"type\": \"paragraph\"}, {\"id\": \"Ut6-hGO47p\", \"data\": {\"text\": \"Vây quanh con trùm này là một đống đàn em đủ thể loại từ khiên lớn, tay dài cho đến giáo mác… Mà để vừa dọn dẹp hết đám tiểu tốt, vừa đánh gục con trùm có tới 2 mạng thì chắc chắn đó là điều không tưởng. Chưa kể, chỉ cần trúng 2 đòn cũng đủ để bạn toi rồi!\"}, \"type\": \"paragraph\"}, {\"id\": \"oGOmKkunYw\", \"data\": {\"text\": \"Tuy nhiên, cũng tại khu vực này, game bố trí cho người chơi một cộng sự đắc lực là một NPC Samurai chiến đấu rất hăng máu nhưng rất… “óc chó” vì chỉ biết lăn xả; bên cạnh đó còn có một hành lang đủ bí mật để người chơi có thể lén lút lẻn lui sau lưng tiêu diệt từng tên tiểu tốt một, “xiên” luôn một trong hai mạng của Juzou The Drunkard.\"}, \"type\": \"paragraph\"}, {\"id\": \"La-v3ofX_-\", \"data\": {\"text\": \"Cuộc chiến giờ đây trở nên dễ thở hơn khi cuộc chơi trở thành 2 vs. 1, bạn và NPC Samurai sẽ liên thủ để tiêu diệt mạng còn lại của con trùm phụ khốn kiếp này!\"}, \"type\": \"paragraph\"}, {\"id\": \"0QNYqSWZED\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685453006909.webp\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}, {\"id\": \"7QBkf1Rj8B\", \"data\": {\"text\": \"Không chỉ riêng việc quan sát và làm chủ đấu trường là điểm mấu chốt giúp Sekiro: Shadows Die Twice dễ thở hơn, mà nếu tìm hiểu kỹ đặc tính của các vật dụng nhặt được thì người chơi cũng có thể có được những lợi thế nhất định trong quá trình dợt trùm.\"}, \"type\": \"paragraph\"}, {\"id\": \"X2Hz2Mlw1H\", \"data\": {\"text\": \"Nhưng do số lượng một số vật dụng tương đối khó kiếm, cũng như tránh việc lạm dụng, thì tốt nhất người chơi vẫn phải dựa vào kỹ năng chiến đấu của bản thân là chủ yếu.\"}, \"type\": \"paragraph\"}, {\"id\": \"ifH6hnbnxP\", \"data\": {\"text\": \"Qua đó, Sekiro: Shadows Die Twice cho người chơi thấy sự tinh tế rất cao của mình trong việc thiết kế và bố trí các chi tiết trong màn chơi.\"}, \"type\": \"paragraph\"}, {\"id\": \"avA2w4tk3N\", \"data\": {\"text\": \"Đừng chỉ hau háu lao vào chiến đấu như một kẻ “hữu dũng vô mưu” mà còn phải biết tận dụng tất cả những lợi thế mà mình có để vượt qua thử thách.\"}, \"type\": \"paragraph\"}, {\"id\": \"WD4n4jgUHG\", \"data\": {\"text\": \"Bạn là một shinobi (nhẫn giả), không phải một samurai (võ sĩ đạo) với tinh thần chiến đấu một sống hai chết.\"}, \"type\": \"paragraph\"}, {\"id\": \"xUFt8chu8d\", \"data\": {\"text\": \"Nhớ lấy điều đó!\"}, \"type\": \"paragraph\"}, {\"id\": \"mBotJajTur\", \"data\": {\"text\": \"HÃY CHIẾN ĐẤU THEO CÁCH CỦA BẠN\", \"level\": 3}, \"type\": \"header\"}, {\"id\": \"kSlX0CA4R1\", \"data\": {\"text\": \"Tạo nên một cơ chế chiến đấu nặng về áp chế và tạo sức ép lên đối thủ,&nbsp;Sekiro: Shadows Die Twice&nbsp;trong khoảng 30% thời lượng đầu tiên dễ khiến game thủ cảm thấy hơi “ngột” do trùng lặp và quá phụ thuộc vào Death blow.\"}, \"type\": \"paragraph\"}, {\"id\": \"0lTRiWtIEX\", \"data\": {\"text\": \"Nhưng càng đi sâu hơn về sau, những kỹ năng mới, những đao pháp mới mới thực sự biến&nbsp;Sekiro: Shadows Die Twice&nbsp;trở thành một tựa game cực kỳ đa dạng phong cách chơi.\"}, \"type\": \"paragraph\"}, {\"id\": \"RdGaEL6mud\", \"data\": {\"text\": \"Nhiều người cho rằng&nbsp;Sekiro: Shadows Die Twice&nbsp;có kho vũ khí nghèo nàn chỉ có một thanh kiếm duy nhất thường xuyên sử dụng.\"}, \"type\": \"paragraph\"}, {\"id\": \"pNorjxPydw\", \"data\": {\"text\": \"Tuy nhiên, họ quên rằng cánh tay giả mới là thứ tạo nên sự đa dạng trong lối chiến đấu của nhân vật chính chứ không phải dăm ba cái chặt, cái chém từ thanh Katana.\"}, \"type\": \"paragraph\"}, {\"id\": \"QIwbB-3OQx\", \"data\": {\"text\": \"Trong&nbsp;Sekiro: Shadows Die Twice, tùy thuộc vào khả năng lùng sục bản đồ của người chơi mà nhân vật chính sẽ sở hữu nhiều hay ít những bộ công cụ nâng cấp cánh tay giả.\"}, \"type\": \"paragraph\"}, {\"id\": \"mBW7bH3QvG\", \"data\": {\"text\": \"Nhờ những nâng cấp này, cánh tay giả của Độc Thủ Lang chúng ta sẽ có những biến thể khác nhau và cũng có những công dụng khác nhau, ứng với những trường hợp sử dụng khác nhau để đạt hiệu quả trong chiến đấu.\"}, \"type\": \"paragraph\"}, {\"id\": \"F7rE6bYrna\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685453089360.webp\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}, {\"id\": \"_5Fpu5p-Fb\", \"data\": {\"text\": \"Chẳng hạn nếu yêu thích lối chơi càn quét và mạnh bạo, cây rìu chiến bật ra từ cánh tay giả có thể giúp Độc Thủ Lang phá nát khiên của đối thủ, đánh bật đòn tất công hay làm đầy thanh Posture của đối tăng lên nhanh chóng.\"}, \"type\": \"paragraph\"}, {\"id\": \"KmF9w0LUtX\", \"data\": {\"text\": \"Hoặc nếu biết được con trùm nào sợ lửa thì chắc chắn biến thể súng lửa từ cánh tay này sẽ giúp người chơi lấy thế thượng phong. Hoặc nếu thích phong cách chiến đấu chắc chắn và phản đòn chớp nhoáng, những tán ô thép sẽ là một lá chắn phòng thủ hiệu quả khỏi cả những đòn sát thương chí mạng “chữ đỏ”.\"}, \"type\": \"paragraph\"}, {\"id\": \"HXJxBc8S0i\", \"data\": {\"text\": \"Không chỉ dừng lại ở cách sử dụng chúng, mà về sau game thủ còn có thể phối hợp thuộc tính và đặc tính của chúng lại với nhau để tạo nên những biến thể mạnh mẽ hơn để áp chế những con trùm dai nhách.\"}, \"type\": \"paragraph\"}, {\"id\": \"0gCSPjW-f9\", \"data\": {\"text\": \"Số lượng biến thể cơ bản có tìm được cho cánh tay giả lên tới con số 10, mà khi kết hợp đặc tính với nhau các kiểu về sau thì con số này còn có thể tăng lên gấp bội.\"}, \"type\": \"paragraph\"}, {\"id\": \"IkEbDzzjfX\", \"data\": {\"text\": \"Tuy nhiên để tránh lạm dụng, mỗi công cụ từ cánh tay giả này sẽ tiêu tốn một lượng “bùa” (Spirit Emblem) nhất định, và số lượng bùa mà Độc Thủ Lang có thể mang đi cũng bị hạn chế.\"}, \"type\": \"paragraph\"}, {\"id\": \"SRYCXiNAOt\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685453111245.webp\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}, {\"id\": \"K_TYYugXfg\", \"data\": {\"text\": \"May mắn là các lá bùa này có thể kiếm khá dễ nên ở giai đoạn đầu game, game thủ nên tích trữ và mua cho mình một số lượng lớn bùa để dùng dần về sau.\"}, \"type\": \"paragraph\"}, {\"id\": \"_odwEJUA_b\", \"data\": {\"text\": \"Một phần cũng là bởi ở nửa đầu game không có nhiều vật phẩm đáng để bỏ tiền ngoài các lá bùa sử dụng cho cánh tay giả, một phần cũng là bởi giá bán của chúng rất rẻ, càng về sau thì càng đắt đỏ.\"}, \"type\": \"paragraph\"}, {\"id\": \"3z9Z5fEK1m\", \"data\": {\"text\": \"Một điểm nhấn sáng giá khác giúp lối chơi của Sekiro: Shadows Die Twice thêm phần đa dạng chính là bộ tập hợp các kỹ năng chiến đấu, phân chia làm hai loại là Ninjutsu (nhẫn thuật) và Combat Art (nghệ thuật giao đấu).\"}, \"type\": \"paragraph\"}, {\"id\": \"i1PzW2O7Gl\", \"data\": {\"text\": \"Ninjutsu thì chắc vốn khá quen thuộc với những ai mê Naruto, nhưng hơi đáng thất vọng là số lượng Ninjutsu của Sekiro: Shadows Die Twice khá ít, chỉ có 3 tuyệt kỹ và thật sự thì cũng chỉ có Puppeteer Ninjutsu là thiết thực mà thôi.\"}, \"type\": \"paragraph\"}, {\"id\": \"fC-s8wyClu\", \"data\": {\"text\": \"Đổi lại, Combat Art lại là một trong những hệ thống rất lý thú, thay thế cho Weapon Art trước đây của các game Souls-Borne.\"}, \"type\": \"paragraph\"}, {\"id\": \"aq6sr4rnlW\", \"data\": {\"text\": \"Có thể hiểu Combat Art chính là những bí kíp võ công được thể hiện dưới dạng các cây kỹ năng.\"}, \"type\": \"paragraph\"}, {\"id\": \"gVDOef-Rwt\", \"data\": {\"text\": \"Mỗi khi lượm, nhận, mua được một cuốn bí kíp nào đó, một nhánh kỹ năng mới sẽ được mở ra và dựa vào những món võ công học được từ các bí kíp này sẽ mang đến một lợi thế vô cùng lớn, đặc biệt là ở các trận đấu trùm.\"}, \"type\": \"paragraph\"}, {\"id\": \"G5xspG7JWu\", \"data\": {\"text\": \"Nhưng cũng giống các biến thể của cánh tay giả, việc sử dụng chúng sao cho phù hợp, đúng tình huống thì lại cần phải bỏ thời gian nghiên cứu chứ không phải bạ đâu dùng đấy hay áp dụng với đối thủ nào cũng như nhau.\"}, \"type\": \"paragraph\"}, {\"id\": \"zESV25TJIT\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685453140153.webp\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}, {\"id\": \"1hv8VAdQrb\", \"data\": {\"text\": \"Một số Combat Art có đòn liên hoàn (combo) lớn, nhưng khả năng gây “sốc” thanh Posture lại khá kém, chỉ phù hợp để đánh dợt quái.\"}, \"type\": \"paragraph\"}, {\"id\": \"RBnO_okMVj\", \"data\": {\"text\": \"Một số Combat Art khác lại có tốc độ chậm và khả năng ra đòn hạn chế nhưng lại có thể tạo được sát thương cao và chóng làm đầy thanh Posture của đối thủ hoặc hồi Posture cho chính mình…\"}, \"type\": \"paragraph\"}, {\"id\": \"NUimHTDnzP\", \"data\": {\"text\": \"Mặt khác, một số Combat Art và tất cả Ninjutsu cũng tiêu tốn Spirit Emblem nên sẽ bắt buộc người chơi cân nhắc kỹ ưu tiên sử dụng cho cánh tay giả hay Combat Art tùy tình huống.\"}, \"type\": \"paragraph\"}, {\"id\": \"sbyvTWHIQ6\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685453153082.webp\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}, {\"id\": \"N6IP1oJpSx\", \"data\": {\"text\": \"ĐỪNG CHẾT THEO CÁCH CỦA BẠN\", \"level\": 3}, \"type\": \"header\"}, {\"id\": \"-Y993BO3LT\", \"data\": {\"text\": \"Cái chết hẳn đã quá quen thuộc với game thủ ở hầu hết các sản phẩm của FromSoftware.\"}, \"type\": \"paragraph\"}, {\"id\": \"bJxiNfuyzA\", \"data\": {\"text\": \"Tuy nhiên, khi mà “chết” được mang hẳn lên tên game như Sekiro: Shadows Die Twice thì chắc chắn đó là một lời nhắc nhở không hề thân thiện từ nhà sản xuất.\"}, \"type\": \"paragraph\"}, {\"id\": \"rT-YDUoXDp\", \"data\": {\"text\": \"Trong Sekiro: Shadows Die Twice, bạn sẽ vẫn “chết như cơm bữa” bởi độ khó của các màn chiến đấu trong game, nhưng mọi chuyện không đơn giản chỉ là chơi lại ở điểm lưu gần nhất.\"}, \"type\": \"paragraph\"}, {\"id\": \"DRi9QL76j6\", \"data\": {\"text\": \"Hình phạt mới mà nhà sản xuất đưa ra khiến game thủ không thể khinh thường cái chết trong game chính là: Dragon Rot và Unseen Aid.\"}, \"type\": \"paragraph\"}, {\"id\": \"CPp137K0gF\", \"data\": {\"text\": \"Dragon Rot là một căn bệnh truyền nhiễm tác động lên các NPC trong game nếu người chơi chết quá nhiều lần.\"}, \"type\": \"paragraph\"}, {\"id\": \"1N1Ac7Ws5g\", \"data\": {\"text\": \"Một khi bị nhiễm Dragon Rot, mọi tương tác của NPC đến thế giới game, chức năng của NPC cũng như các tuyến nhiệm vụ, cốt truyện liên quan đến họ đều bị gián đoạn ngay lập tức.\"}, \"type\": \"paragraph\"}, {\"id\": \"I9_R0ElKkg\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685453183508.webp\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}, {\"id\": \"WW8er1w1JB\", \"data\": {\"text\": \"Tuy không đến nỗi lăn đùng ra chết như nhiều lời đồn thổi, nhưng tác hại mà Dragon Rot gây ra không phải là nhỏ bởi nó sẽ ảnh hưởng mạnh đến hành trình của nhân vật chính, khả năng nâng cấp hay là cả cốt truyện của game.\"}, \"type\": \"paragraph\"}, {\"id\": \"U5UyL-0yAj\", \"data\": {\"text\": \"May mắn là game thủ có thể chữa lành căn bệnh này nếu chịu khó lắng nghe và tìm hiểu chức năng của một số NPC trong game.\"}, \"type\": \"paragraph\"}, {\"id\": \"ENlIKQNcoT\", \"data\": {\"text\": \"Nhưng lời khuyên vẫn là đừng xem thường cái chết, dù cho bạn có thể hồi sinh được dăm ba lần đi nữa, bởi có khi, bạn sẽ mãi chẳng thể cứu được NPC nào vì không có đủ thuốc chữa dù đã tìm được cách chữa.\"}, \"type\": \"paragraph\"}, {\"id\": \"gnq0a9fCia\", \"data\": {\"text\": \"Ngoài Dragon Rot, Unseen Aid tuy là một cơ chế phụ nhưng đôi khi sẽ giúp ích cho người chơi khá nhiều, giúp giảm bớt lượng tiền và điểm kỹ năng tích lũy được sau các lần chết.\"}, \"type\": \"paragraph\"}, {\"id\": \"xSLswijORK\", \"data\": {\"text\": \"Tỉ lệ xuất hiện phép màu Unseen Aid là 30% và chúng sẽ giảm dần nếu người chơi chết quá nhiều lần.\"}, \"type\": \"paragraph\"}, {\"id\": \"vpt__7yo_p\", \"data\": {\"text\": \"Cũng để giảm bớt gánh nặng về cái chết, Sekiro: Shadows Die Twice cũng giới thiệu một cơ chế cho bạn chết đi sống lại nhiều lần.\"}, \"type\": \"paragraph\"}, {\"id\": \"8aPoqr8aaE\", \"data\": {\"text\": \"Về cơ bản thì người chơi có khoảng 2 mạng, tối đa là 4 mạng, và sau mỗi lần chết chỉ có thể sống lại một lần duy nhất và buộc phải thực hiện một lần Death Blow mới cho phép hồi sinh tiếp một lần nữa.\"}, \"type\": \"paragraph\"}, {\"id\": \"FIfiBs0sCY\", \"data\": {\"text\": \"Khó nhằn hơn, nếu lỡ sử dụng các lần hồi sinh có biểu tượng lá hoa anh đào, thì chúng cũng cần số yêu cầu mới cho game thủ hồi lại được lần hồi sinh đó, mà thường sẽ cần một số lượng lớn Death Blow mới có thể phục hồi.\"}, \"type\": \"paragraph\"}, {\"id\": \"2nHBDyTBu4\", \"data\": {\"text\": \"Vậy nên, đừng nghĩ mình bất tử!\"}, \"type\": \"paragraph\"}, {\"id\": \"8MQdppRTIQ\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685453212557.webp\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}, {\"id\": \"izDpR-zXuW\", \"data\": {\"text\": \"VÀ NHỮNG KẺ BAN PHÁT… CÁI CHẾT\", \"level\": 3}, \"type\": \"header\"}, {\"id\": \"IH3FV5myaF\", \"data\": {\"text\": \"Ôi những con trùm, những cơn ác mộng của mọi tựa game đến từ FromSoftware. Nếu bạn đã kinh qua đủ bộ Dark Souls, Demon’s Souls, BloodBorne thì thú thật đến Sekiro: Shadows Die Twice, những con trùm vẫn là một tấn ác mộng thực sự.\"}, \"type\": \"paragraph\"}, {\"id\": \"M_jZ_gt7-K\", \"data\": {\"text\": \"Có tất cả tận 51 con trùm cả lớn lẫn bé, cả chính lẫn phụ để người chơi tha hồ dợt hết New Game này đến New Game+ khác.\"}, \"type\": \"paragraph\"}, {\"id\": \"qeb_RissPp\", \"data\": {\"text\": \"Nhưng dù cho số lượng của chúng có lên tới hơn 50 con nhưng lại chẳng có con nào giống con nào, cách đối phó mỗi con trùm lại cũng chẳng có con nào giống nhau hoàn toàn.\"}, \"type\": \"paragraph\"}, {\"id\": \"O6hK_ikHSn\", \"data\": {\"text\": \"Kể cả khi bạn đụng độ lại những con trùm cũ thì chúng cũng thừa sức cho bạn “ngập một mồm hành”.\"}, \"type\": \"paragraph\"}, {\"id\": \"I0dU90FHN2\", \"data\": {\"text\": \"Điển hình có thể kể đến như Lady Butterfly (cũng có thể xem là một tutorial-boss) với cách chiến đấu rất tinh quái với thân pháp biến ảo, Juzou The Drunkard như đã nói ở trên, Seven Ashina Spears với siêu sát thương và lượng máu “trâu bò”, Guardian Ape với pha “nhặt đầu” gây ám ảnh cho game thủ, Armored Warrior với lớp giáp không thể xuyên phá, hay ấn tượng nhất với người viết là Demon of Hatred – một con trùm mang phong vị của Souls-Borne với độ khó điên rồ.\"}, \"type\": \"paragraph\"}, {\"id\": \"_2Ha07yv0F\", \"data\": {\"text\": \"Một số con trùm trong game cũng có chiều sâu nhất định khi luôn có những câu chuyện đằng sau sự có mặt của chúng, lý do chính hành động và những tình tiết liên quan đến cốt truyện.\"}, \"type\": \"paragraph\"}, {\"id\": \"rYNj7UQkJ6\", \"data\": {\"text\": \"Từ đó mở ra những đặc thù trong cách chiến đấu theo từng giai đoạn khác nhau tùy theo trình tự phiêu lưu ký của nhân vật chính.\"}, \"type\": \"paragraph\"}, {\"id\": \"UsbKiaB5ne\", \"data\": {\"text\": \"Đây luôn là đặc sản mà những sản phẩm của FromSoftware khiến game thủ ăn ngủ không yên, và những cái tên Youtuber nổi tiếng cũng có dịp để phân tích những hàm ý sâu xa, những bài học đầy tính nhân văn, hay cho game thủ một cái nhìn khác về số phận và cuộc đời.\"}, \"type\": \"paragraph\"}, {\"id\": \"UbFVGI2Pd1\", \"data\": {\"text\": \"Và cũng như cách làm của các tựa game khác của FromSoftware, mỗi game thủ hãy hiểu lấy những điều này theo cách nhìn nhận của họ.\"}, \"type\": \"paragraph\"}, {\"id\": \"yyxC-Ybx9f\", \"data\": {\"text\": \"Với số lượng trùm khủng như vậy, mỗi lượt hoàn thành Sekiro: Shadows Die Twice sẽ kéo dài không dưới 20 giờ nếu game thủ chọn cách đối đầu hết tất cả những kẻ ngán đường.\"}, \"type\": \"paragraph\"}, {\"id\": \"T4XD6aMkTC\", \"data\": {\"text\": \"Nhưng cũng may mắn là nếu tinh ý thì game thủ vẫn có thể giảm bớt được gánh nặng khi một số con trùm phụ không thực sự quá quan trọng và có thể ngó lơ nhờ vào cách thiết kế môi trường đầy lắc léo quen thuộc của “thánh” Mayazaki.\"}, \"type\": \"paragraph\"}, {\"id\": \"P-ezOvdamy\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685453250250.webp\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}, {\"id\": \"XW8oiVV6hk\", \"data\": {\"text\": \"Nhắc đến game của FromSoftware mà không nói qua thiết kế bản đồ thì cũng đúng là thiếu sót, bởi nếu so với các Dark Souls hay BloodBorne thì Sekiro: Shadows Die Twice rộng lớn hơn khá nhiều.\"}, \"type\": \"paragraph\"}, {\"id\": \"WdyOPtEjj0\", \"data\": {\"text\": \"Một phần do khả năng di chuyển của nhân vật chính nay đã linh động hơn nên bản đồ rộng hơn cũng là điều dễ hiểu.\"}, \"type\": \"paragraph\"}, {\"id\": \"rw2aMNdYo1\", \"data\": {\"text\": \"Mức độ liên kết và sắp sếp theo các trật tự theo dạng mở mà không mở (có thể xem như một thế giới bán mở (semi-open world) na ná God of War), cũng được thực hiện ở mức độ cực tốt.\"}, \"type\": \"paragraph\"}, {\"id\": \"rrrhEqHWdA\", \"data\": {\"text\": \"Dù cho có bay nhảy thoải mái đến đâu thì đôi lúc game thủ cũng phải ngáo ngơ không biết tiếp theo mình phải đi đâu hoặc đã đi qua những đâu vì độ phức tạp và đan xen khá rối rắm của thế giới trong Sekiro: Shadows Die Twice.\"}, \"type\": \"paragraph\"}, {\"id\": \"Dlrgr-s3Au\", \"data\": {\"text\": \"Thiết nghĩ, bạn đọc nên trực tiếp khám phá game thì sẽ nhìn thấy nhiều sự thú vị hơn thay vì để người viết nói về điều này.\"}, \"type\": \"paragraph\"}, {\"id\": \"SqphN4JuEC\", \"data\": {\"text\": \"BẠN SẼ GHÉT\", \"level\": 3}, \"type\": \"header\"}, {\"id\": \"pTFJ5QipgI\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685453279132.webp\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}, {\"id\": \"PWqNh-cYww\", \"data\": {\"text\": \"Câu trả lời mang tính chủ quan của người viết có lẽ là KHÔNG!\"}, \"type\": \"paragraph\"}, {\"id\": \"wwzXI7XT1F\", \"data\": {\"text\": \"Sekiro: Shadows Die Twice có thể là một cái tên mới đầy tính sáng tạo và đột phá trong lối chơi lẫn thiết kế.\"}, \"type\": \"paragraph\"}, {\"id\": \"5K4eSMMV9-\", \"data\": {\"text\": \"Nhưng nếu bảo rằng Sekiro: Shadows Die Twice hay hơn các tượng đài trước đây mà FromSoftware đã gầy dựng thì cần phải xem xét lại bởi có khá nhiều điểm mà game chưa đạt đến độ chín của một “siêu phẩm”.\"}, \"type\": \"paragraph\"}, {\"id\": \"a8spTLFoNG\", \"data\": {\"text\": \"Đầu tiên là âm nhạc.\"}, \"type\": \"paragraph\"}, {\"id\": \"IYl3coerqh\", \"data\": {\"text\": \"Âm nhạc là linh hồn của mọi tựa game Souls-Borne, cái thế giới đen tối của Souls-Borne khắc họa được phải đến một nửa là nhờ công của những bản nhạc nền đầy tuyệt vọng đè nặng lên tinh thần của chính game thủ.\"}, \"type\": \"paragraph\"}, {\"id\": \"ejRHDmYvlj\", \"data\": {\"text\": \"Mỗi giai điệu mà Sekiro: Shadows Die Twice vang lên lại như chỉ tập trung vào tiết tấu của trận đấu mà không góp phần tôn lên cái không khí, không góp phần khắc họa sự suy đồi và biến chất của những con người thèm khát sự bất tử!\"}, \"type\": \"paragraph\"}, {\"id\": \"Gjrj9iiSkW\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685453303662.webp\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}, {\"id\": \"HZLTYTiG8w\", \"data\": {\"text\": \"Âm nhạc của Sekiro: Shadows Die Twice nói thật không phải là dở, ngược lại còn hay khi phối hợp nhiều nhạc cụ cổ điển của người Á Đông và mang đậm âm sắc của Nhật Bản, nhưng buồn là chúng lại quá lạc quẻ.\"}, \"type\": \"paragraph\"}, {\"id\": \"EualHtj0Ih\", \"data\": {\"text\": \"Mà cũng vì vậy, hành trình của Độc Thủ Lang lại trở nên thiếu chiều sâu, thiếu những nốt trầm để game thủ có thể suy tư, hoặc cảm thấy rùng mình trước khi đối diện với một con trùm – thứ mà có thể khiến game thủ rơi vào trạng thái ưu phiền với phạm trù đạo đức của bản thân khi phải chiến đấu với chúng.\"}, \"type\": \"paragraph\"}, {\"id\": \"7fBd5NZnlw\", \"data\": {\"text\": \"Lạc quẻ nhưng hay thì còn tạm chấp nhận, nhưng quá ít giai điệu và lặp đi lặp lại, dùng không đúng chỗ thì Sekiro: Shadows Die Twice thường xuyên mắc phải.\"}, \"type\": \"paragraph\"}, {\"id\": \"eroK2PdZ1H\", \"data\": {\"text\": \"Một bản nhạc nền dùng cho chiến đấu luôn được bật lên vô tội vạ, kể cả khi chỉ một con… thằn lằn xuất hiện cũng đủ khiến không khí trở nên “nghiêm trọng” một cách thái quá!\"}, \"type\": \"paragraph\"}, {\"id\": \"etVKlZTCZ2\", \"data\": {\"text\": \"Hình ảnh trong Sekiro: Shadows Die Twice cũng để lại một số điểm trừ nhỏ bởi sự lạc hậu về công nghệ của nó.\"}, \"type\": \"paragraph\"}, {\"id\": \"v4cpXbw-uF\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685453321324.webp\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}, {\"id\": \"ssSrZ0gY6N\", \"data\": {\"text\": \"Xét trên khía cạnh một thế giới u ám và ảm đạm như Dark Souls hay BloodBorne, những gì được dựng trên nền engine này là ấn tượng và phù hợp, nhưng khi bước qua một thế giới đầy sắc màu và tươi sáng như Sekiro: Shadows Die Twice thì nền đồ họa này bộc lộ quá nhiều nét thô kệch dù cho nhà phát triển đã cố gắng “lấp liếm” bằng hàng tá hiệu ứng thị giác xa gần khác nhau.\"}, \"type\": \"paragraph\"}, {\"id\": \"tUtHj0wsZW\", \"data\": {\"text\": \"Các khung cảnh khiến người chơi phải trầm trồ cũng hiếm khi có do góc nhìn camera quá hẹp và thấp, nên hy vọng trong tương lai sẽ game sẽ có Photo Mode.\"}, \"type\": \"paragraph\"}, {\"id\": \"XWKxfzdagu\", \"data\": {\"text\": \"Dù sao, đồ họa lỗi thời cũng chỉ là một điểm trừ nhỏ bởi từ trước đến nay game của FromSoftware cũng chưa từng được đánh giá cao cho lắm.\"}, \"type\": \"paragraph\"}, {\"id\": \"TIY3D2SlSh\", \"data\": {\"text\": \"Mà điểm trừ lớn hơn hơn là ở sự cân bằng trong các cơ chế được nhét vào game.\"}, \"type\": \"paragraph\"}, {\"id\": \"KjwyH7vPCw\", \"data\": {\"text\": \"Đúng là Sekiro: Shadows Die Twice mở ra một hệ thống chiến đấu quá mới mẻ, phức tạp hơn và cũng đa dạng hơn từ Ninjutsu cho đến Combat Art hay cánh tay giả với nhiều biến thể độc đáo.\"}, \"type\": \"paragraph\"}, {\"id\": \"uidf_FB06P\", \"data\": {\"text\": \"Nhưng khả năng cân bằng và thể hiện chức năng giữa các hệ thống này lại hơi thiếu cân bằng do hiệu quả chúng đem lại không nhiều, đôi khi là phiền phức khiến game thủ gần như nói không với một số tính năng, chẳng hạn như Ninjutsu…\"}, \"type\": \"paragraph\"}]}'),(40,'Đánh giá The Witcher 3','ca20a040b7e7dbf11f954b4fa85e1ecdcf8f95eeba8ebf71f89455794eec80f2.jpg','The Witcher 3 - Game hay cần chơi ngay',19,2,6,'2023-05-30 00:00:00',20,1,'none',1,'{\"blocks\": [{\"id\": \"1RW-HKw5pZ\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685454033835.jpg\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}]}'),(41,'Đánh giá Liên quân Mobile','img-share.jpg','Liên Quân Mobile - Game cho người thành công ?',19,2,6,'2023-05-30 00:00:00',0,1,'none',1,'{\"blocks\": [{\"id\": \"ICrLj8Kfnl\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685454057036.jpg\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}]}'),(42,'Đánh giá Liên minh huyền thoại - Tốc chiến','thumb_main.png','Wildrift - Liệu có thể soán ngôi Liên quân tại Việt Nam',19,2,6,'2023-05-29 00:00:00',0,1,'none',1,'{\"blocks\": [{\"id\": \"pt-nPeldcB\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685454122280.png\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}]}'),(43,'The Simp 4 - Số phận giờ ra sao','14dc51b0-344e-11ed-bf58-673a821ec893.cf.jpg','Game hay 1 thời giờ ra sao khi rơi vào tay EA',19,2,4,'2023-01-02 00:00:00',0,1,'none',1,'{\"blocks\": [{\"id\": \"riwaYPCahj\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685456355519.jpg\"}, \"caption\": \"\", \"stretched\": false, \"withBorder\": false, \"withBackground\": false}, \"type\": \"image\"}]}'),(44,'Hướng dẫn chơi Joker','99e61bc305c2debe25cdea690384ca2359d1abefd8b44.jpg','Joker Gottham hay Tuấn Cùi Bến Tre',6,3,4,'2023-01-02 00:00:00',0,1,'none',1,'{\"blocks\": [{\"id\": \"ypnsFSYpCa\", \"data\": {\"file\": {\"url\": \"http://localhost:3030/public/images/1685456403057.jpg\"}, \"caption\": \"\", \"stretched\": false